| grades | CREATE TABLE `grades` (
  `pk_grade_ID` int(255) unsigned NOT NULL AUTO_INCREMENT,
  `student_id` int(255) unsigned NOT NULL,
  `grade` decimal(5,2) NOT NULL DEFAULT '0.00',
  `school_code` enum('L','B','A','F','E','T','I','W','S','U','M') NOT NULL,
  `dept_id` int(255) unsigned NOT NULL,
  `course_code` char(5) DEFAULT NULL,
  PRIMARY KEY (`pk_grade_ID`,`student_id`,`grade`),
  KEY `student_id` (`student_id`),
  KEY `school_code` (`school_code`,`dept_id`,`course_code`),
  CONSTRAINT `grades_ibfk_2` FOREIGN KEY (`school_code`, `dept_id`, `course_code`) REFERENCES `courses` (`school_code`, `dept_id`, `course_code`),
  CONSTRAINT `grades_ibfk_1` FOREIGN KEY (`student_id`) REFERENCES `students` (`student_id`)
) ENGINE=InnoDB AUTO_INCREMENT=32 DEFAULT CHARSET=utf8 |
